package hw3Q2JavaVariables;

public class AboutMe {
// declaring a variable
	String Sanjida123= "I am Sanjida";;
	char Boy='B';
	int Calculator= 4; 
	boolean Statement= true;
	byte Compute= 10;
	short Addition= 454;
	long Multiplication= 454L;
	float Math= 5.12345F;
	double Science= 5.1234567891;
	
		
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	 // Instantiating 'AboutMe' object
	 
	 
		AboutMe Sanjida = new AboutMe();
		Sanjida.Sanjida123 = "test";
		System.out.println("This is an example of string object used from my AboutMe object:" + Sanjida.Sanjida123);
		System.out.println("This is an example of char object used from my AboutMe object:" + Sanjida.Boy);
		System.out.println("This is an example of int object used from my AboutMe object:" + Sanjida.Calculator);
		System.out.println("This is an example of boolean object used from my AboutMe object:" + Sanjida.Statement);
		System.out.println("This is an example of byte object used from my AboutMe object:" + Sanjida.Compute);
		System.out.println("This is an example of short object used from my AboutMe object:" + Sanjida.Addition);
		System.out.println("This is an example of long object used from my AboutMe object:" + Sanjida.Multiplication);
		System.out.println("This is an example of float object used from my AboutMe object:" + Sanjida.Math);
		System.out.println("This is an example of double object used from my AboutMe object:" + Sanjida.Science);
		;
	 }

} 
